import "core-js/modules/es.array.push.js";
import { listByQo } from "@/api/personnel_management/department/deptApi";
import { popup } from "@/assets/js/common";
import { deactivate, detail, editBtnClick, pageByQo, resetPwd, save, update } from "@/api/personnel_management/employee/empApi";
import { getAllRole, queryRoleIdsByEid, saveRoleEmp } from "@/api/system/role/roleApi";
import { clearCookie, loginEmp } from "@/assets/js/auth";
export default {
  data() {
    return {
      BaseApi: this.$store.state.BaseApi,
      detailVisable: false,
      newVisable: false,
      editVisable: false,
      empRoleForm: {},
      hasRoleIds: [],
      allRoles: [],
      detailForm: {},
      searchForm: {},
      editForm: {},
      options: [],
      tableData: [],
      resetPwdVisable: false,
      newForm: {},
      empRoleVisable: false,
      checked_keys: [],
      roles: [],
      resetPwdForm: {},
      rules: {
        username: [{
          required: true,
          message: "手机号不能为空",
          trigger: "blur"
        }],
        nickName: [{
          required: true,
          message: "姓名不能为空",
          trigger: "blur"
        }],
        idCard: [{
          required: true,
          message: "身份证号不能为空",
          trigger: "blur"
        }]
      },
      /*上传头像*/
      imageUrl: ""
    };
  },
  methods: {
    deptAll() {
      listByQo({}).then(res => {
        res = res.data;
        if (res.code == 200) {
          this.options = res.data;
          popup("操作成功");
        } else {
          popup(res.msg, "error");
        }
      });
    },
    getRoleAll() {
      getAllRole().then(res => {
        res = res.data;
        if (res.code == 200) {
          this.allRoles = res.data;
        } else {
          popup(res.msg, "warning");
        }
      });
    },
    init() {
      pageByQo(this.searchForm).then(res => {
        res = res.data;
        if (res.code == 200) {
          this.tableData = res.data.records;
          this.searchForm.total = res.data.total;
          this.searchForm.pageSize = res.data.size;
          this.searchForm.currentPage = res.data.current;
        } else {
          popup(res.msg, "error");
        }
      });
    },
    /*分页*/
    handleSizeChange(val) {
      this.searchForm.pageSize = val;
      this.init();
      console.log(`每页 ${val} 条`);
    },
    handleCurrentChange(val) {
      this.searchForm.currentPage = val;
      this.init();
      console.log(`当前页: ${val}`);
    },
    submitSearchForm() {
      this.init();
    },
    detailBtn(row) {
      detail(row.id).then(res => {
        res = res.data;
        if (res.code == 200) {
          this.detailForm = res.data;
          this.detailForm.deptName = row.deptName;
          this.detailVisable = true;
        } else {
          popup(res.msg, "error");
        }
      });
      console.log(this.detailForm);
    },
    detail_down() {
      this.detailForm = {};
      this.detailVisable = false;
    },
    /*上传头像*/
    handleAvatarSuccess(res) {
      this.imageUrl = res.url;
    },
    beforeAvatarUpload(file) {
      const isJPG = file.type === "image/jpeg" || file.type === "image/png";
      const isLt2M = file.size / 1024 / 1024 < 3;
      if (!isJPG) {
        this.$message.error("上传头像图片只能是 JPG或PNG 格式!");
      }
      if (!isLt2M) {
        this.$message.error("上传头像图片大小不能超过 3MB!");
      }
      return isJPG && isLt2M;
    },
    submitNewForm(formName) {
      this.$refs[formName].validate(valid => {
        if (valid) {
          this.newForm.headImg = this.imageUrl;
          save(this.newForm).then(res => {
            res = res.data;
            if (res.code == 200) {
              popup("操作成功");
              this.newForm = {};
              this.imageUrl = "";
              this.newVisable = false;
              this.init();
            } else {
              popup(res.msg, "error");
            }
          });
        }
      });
    },
    saveCancel() {
      this.newVisable = false;
      this.imageUrl = "";
      this.$refs["newForm"].resetFields();
    },
    /*修改员工信息*/
    editCancel() {
      this.editVisable = false;
      this.imageUrl = "";
      this.$refs["editForm"].resetFields();
      this.editForm = {};
    },
    editBtn(id) {
      editBtnClick(id).then(res => {
        res = res.data;
        if (res.code == 200) {
          this.editForm = {
            ...res.data
          };
          console.log(this.editForm);
          this.imageUrl = this.editForm.headImg;
          this.editVisable = true;
        } else {
          popup(res.msg, "error");
        }
      });
    },
    submitEditForm(formName) {
      this.$refs[formName].validate(valid => {
        if (valid) {
          this.editForm.headImg = this.imageUrl;
          update(this.editForm).then(res => {
            res = res.data;
            if (res.code == 200) {
              popup("操作成功");
              this.editForm = {};
              this.imageUrl = "";
              this.editVisable = false;
              this.init();
            } else {
              popup(res.msg, "error");
            }
          });
        }
      });
    },
    /*离职*/
    delBtn(row) {
      this.$confirm("确定要将该员工办理离职?", "警示", {
        confirmButtonText: "确定",
        cancelButtonText: "取消",
        type: "warning"
      }).then(() => {
        if (row.state == "1") {
          popup("已是离职状态", "warning");
          return;
        }
        deactivate(row.id).then(res => {
          res = res.data;
          if (res.code == 200) {
            popup("操作成功");
            this.init();
          } else {
            popup(res.msg, "error");
          }
        });
      }).catch(() => {
        this.$message({
          type: "info",
          message: "已取消操作"
        });
      });
    },
    dialog_imgClose() {
      this.imageUrl = "";
    },
    /*分配角色按钮*/
    empRoleBtn(id) {
      this.empRoleForm.eid = id;
      /*根据员工编号查询角色编号集合*/
      queryRoleIdsByEid(id).then(res => {
        res = res.data;
        if (res.code == 200) {
          this.empRoleForm.empRoleIds = res.data;
          this.hasRoleIds = res.data;
          this.empRoleVisable = true;
        } else {
          popup(res.msg, "warning");
        }
      });
      this.empRoleVisable = true;
    },
    submitRoleBtn() {
      if (JSON.stringify(this.hasRoleIds) === JSON.stringify(this.empRoleForm.empRoleIds)) {
        popup("你未做改动...", "warning");
      } else {
        this.$confirm("确定修改员工的职务?", "警示", {
          confirmButtonText: "确定",
          cancelButtonText: "取消",
          type: "warning"
        }).then(() => {
          this.empRoleForm.empRoleIds = this.empRoleForm.empRoleIds.toString();
          saveRoleEmp(this.empRoleForm).then(res => {
            res = res.data;
            if (res.code == 200) {
              popup("操作成功");
              this.hasRoleIds = [];
              this.empRoleForm = {};
              this.empRoleVisable = false;
            } else {
              this.empRoleForm.empRoleIds = this.hasRoleIds;
              popup(res.msg, "error");
            }
          });
        }).catch(() => {
          this.$message({
            type: "info",
            message: "已取消操作"
          });
        });
      }
    },
    roleBtnCancel() {
      this.empRoleVisable = false;
      this.hasRoleIds = [];
      this.empRoleIds = [];
    },
    /*重置密码按钮*/
    resetPwd(row) {
      this.resetPwdForm = {};
      this.resetPwdForm.eid = row.id;
      this.resetPwdVisable = true;
    },
    submitResetPwd() {
      this.$confirm("确定要重置该账户的密码?", "警示", {
        confirmButtonText: "确定",
        cancelButtonText: "取消",
        type: "warning"
      }).then(() => {
        resetPwd(this.resetPwdForm).then(res => {
          res = res.data;
          if (res.code == 200) {
            this.resetPwdVisable = false;
            var emp = loginEmp();
            if (emp.id == this.resetPwdForm.eid) {
              clearCookie("employee");
              clearCookie("token");
              this.$router.push("/");
            }
            this.resetPwdForm = {};
            popup("密码重置成功");
          } else {
            popup(res.msg, "error");
          }
        });
      }).catch(() => {
        this.$message({
          type: "info",
          message: "已取消操作"
        });
      });
    }
  },
  mounted() {
    /*初始化所有部门信息*/
    this.deptAll();
    this.getRoleAll();
    /*初始化表格数据*/
    this.init();
  }
};
