    upOrdown(gid, suspend, residueNum) {
      this.$confirm("确定要将该商品" + (suspend == "0" ? "停课" : "开课 ") + "？", "警示", {
        confirmButtonText: "确定",
        cancelButtonText: "取消",
        type: "warning"
      }).then(() => {
        upOrdown({
          gid: gid,
          suspend: suspend
        }).then(res => {
          res = res.data;
          if (res.code == 200) {
            if (suspend == "0") {
              popup("停课成功");
            } else {
              popup("开课成功");
            }
            this.init();
          } else {
            popup(res.msg, "error");
          }
        });
      }).catch(() => {
        this.$message({
          type: "info",
          message: "已取消操作"
        });
      });
    },
    resetReturnForm(formName) {
      this.$refs[formName].resetFields();
      this.returnVisible = false;
      this.returnForm = {};
    },
    /*修改*/
    submitEditForm(formName) {
      this.$refs[formName].validate(valid => {
        if (valid) {
          this.editForm.coverUrl = this.imageUrl;
          update(this.editForm).then(res => {
            res = res.data;
            if (res.code == 200) {
              popup("操作成功");
              this.editForm = {};
              this.imageUrl = "";
              this.dialogVisible = false;
              this.init();
            } else {
              popup(res.msg, "error");
            }
          });
        }
      });
    },
    resetEditForm(formName) {
      this.$refs[formName].resetFields();
      this.editForm = {};
      this.dialogVisible = false;
    },
    editBtn(id) {
      queryGoodsById({
        id: id
      }).then(res => {
        res = res.data;
        if (res.code == 200) {
          this.editForm = {
            id: res.data.id,
            name: res.data.name,
            info: res.data.info,
            categoryId: res.data.categoryId,
            sellPrice: res.data.sellPrice,
            purchashPrice: res.data.purchashPrice,
            state: res.data.state,
            coverUrl: res.data.coverUrl,
            suspend: res.data.suspend
          };
          this.imageUrl = this.editForm.coverUrl ? this.editForm.coverUrl : "";
          this.dialogVisible = true;
        } else {
          popup(res.msg, "warning");
        }
      });
    },
    returnBtn(id) {
      this.returnForm = {
        goodsId: "",
        goodsNum: "",
        goodsName: "",
        createTime: "",
        info: "",
        storeId: ""
      };
      queryGoodsById({
        id: id
      }).then(res => {
        res = res.data;
        if (res.code == 200) {
          this.returnForm.goodsId = res.data.id;
          this.returnForm.goodsName = res.data.name;
          this.returnForm.goodsNum = res.data.residueNum;
          if (this.returnForm.goodsNum == 0) {
            popup("货架上没有商品", "warning");
            return;
          }
          this.goodsNum_max = res.data.residueNum;
          this.returnVisible = true;
        } else {
          popup(res.msg, "warning");
        }
      });
    },
    submitReturnForm(formName) {
      this.$refs[formName].validate(valid => {
        if (valid) {
          returnGoods(this.returnForm).then(res => {
            res = res.data;
            if (res.code == 200) {
              popup("退还成功");
              this.returnVisible = false;
              this.init();
            } else {
              popup(res.msg, "error");
            }
          });
        }
      });
    },
    // 调用摄像头权限
    getCompetence() {
      this.$nextTick(() => {
        const _this = this;
        this.os = false;
        this.thisCancas = document.getElementById("canvasCamera");
        this.thisContext = this.thisCancas.getContext("2d");
        this.thisVideo = document.getElementById("videoCamera");
        if (navigator.mediaDevices === undefined) {
          navigator.menavigatordiaDevices = {};
        }
        if (navigator.mediaDevices.getUserMedia === undefined) {
          navigator.mediaDevices.getUserMedia = function (constraints) {
            let getUserMedia = navigator.webkitGetUserMedia || navigator.mozGetUserMedia || navigator.getUserMedia;
            if (!getUserMedia) {
              return Promise.reject(new Error("getUserMedia is not implemented in this browser"));
            }
            return new Promise(function (resolve, reject) {
              getUserMedia.call(navigator, constraints, resolve, reject);
            });
          };
        }
        const constraints = {
          audio: false,
          video: {
            width: _this.videoWidth,
            height: _this.videoHeight,
            transform: "scaleX(-1)"
          }
        };
        navigator.mediaDevices.getUserMedia(constraints).then(function (stream) {
          if ("srcObject" in _this.thisVideo) {
            _this.thisVideo.srcObject = stream;
          } else {
            _this.thisVideo.src = window.URL.createObjectURL(stream);
          }
          _this.thisVideo.onloadedmetadata = function (e) {
            _this.thisVideo.play();
          };
        }).catch(err => {
          this.$notify({
            title: "警告",
            message: "没有开启摄像头权限或浏览器版本不兼容.",
            type: "warning"
          });
        });
      });
    },
